import React, { useContext, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import blogs from '../data/blogs'; // Adjust the path as necessary
import avtar from "../assets/images/avatar.jpg"; // Adjust the path as necessary
import { Container, Row, Col, ListGroup, Alert, Form } from "reactstrap";
import "../styles/Blogdetails.css"; // Adjust the path as necessary
import FeaturedBlogsList from "../components/FeaturedBlogs.jsx/FeaturedBlogsList"; // Adjust the path as necessary
import Subtitle from "../shared/Subtitle"; // Adjust the path as necessary
import Newsletter from "../shared/Newsletter"; // Adjust the path as necessary
import { AuthContext } from "../context/AuthContext"; // Adjust the path as necessary

const BlogDetails = () => {
  const { id } = useParams();
  const blog = blogs.find(blog => blog.id === id);

  if (!blog) {
    return <div>Error: Blog not found!</div>;
  }

  const { title, content, author, date, photo } = blog;
  const { user } = useContext(AuthContext);
  const commentMsgRef = useRef("");
  const [commentStatus, setCommentStatus] = useState(null);
  const [isLoginAlertVisible, setIsLoginAlertVisible] = useState(false);
  const [comments, setComments] = useState(blog.comments || []);

  const submitHandler = (e) => {
    e.preventDefault();

    if (!user) {
      setIsLoginAlertVisible(true);
      return;
    }

    const commentMsg = commentMsgRef.current.value;
    const commentData = {
      comment: commentMsg,
      username: user.username,
    };

    setComments((prevComments) => [...prevComments, commentData]);
    commentMsgRef.current.value = "";
    setCommentStatus("success");
  };

  return (
    <>
      <Container>
        <Row>
          <Col lg="8">
            <div className="blog__content">
              <h2>{title}</h2>
              {photo && <img src={photo} alt={title} />}
              <p>{content}</p>
              <p>Author: {author}</p>
              <p>Date: {date}</p>

              <div className="blog__reviews mt-4">
                <h4>Comments</h4>
                {commentStatus === "success" && (
                  <Alert color="success">Comment added successfully.</Alert>
                )}
                {isLoginAlertVisible && (
                  <Alert color="warning" toggle={() => setIsLoginAlertVisible(false)}>
                    Please log in to add a comment.
                  </Alert>
                )}
                <Form onSubmit={submitHandler}>
                  <div className="review__input">
                    <input
                      type="text"
                      placeholder="Share your thoughts"
                      required
                      ref={commentMsgRef}
                    />
                    <button className="primary__btn text-white" type="submit">
                      Submit
                    </button>
                  </div>
                </Form>
                <ListGroup className="user__reviews">
                  {comments.map((comment, index) => (
                    <div className="review__item" key={index}>
                      <img src={avtar} alt="User Avatar" />
                      <div className="w-100">
                        <h5>{comment.username}</h5>
                        <h6>{comment.comment}</h6>
                      </div>
                    </div>
                  ))}
                </ListGroup>
              </div>
            </div>
          </Col>
          <Col lg="4">
            <div className="Featured__blogs">
              <div className="title">
                <Subtitle subtitle={"Featured Blogs"} />
              </div>
              <div className="mx-auto md:text-center">
                <FeaturedBlogsList lg={11} md={10} sm={11} />
              </div>
            </div>
          </Col>
        </Row>
      </Container>
      <Newsletter />
    </>
  );
};

export default BlogDetails;
