import { createContext, useEffect, useReducer } from "react";

// Initial state object
const initial_state = {
    user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null,
    loading: false,
    error: null,
};

// Create context
export const AuthContext = createContext(initial_state);

// Reducer function to manage auth state
const AuthReducer = (state, action) => {
    switch (action.type) {
        case 'LOGIN_START':
            return {
                user: null,
                loading: true,
                error: null,
            };

        case 'LOGIN_SUCCESS':
            return {
                user: action.payload,
                loading: false,
                error: null,
            };

        case 'LOGIN_FAILURE':
            return {
                user: null,
                loading: false,
                error: action.payload,
            };

        case 'REGISTER_SUCCESS':
            return {
                user: null,
                loading: false,
                error: null,
            };

        case 'LOGOUT':
            return {
                user: null,
                loading: false,
                error: null,
            };

        default:
            return state;
    }
};

// AuthContextProvider component that wraps around the app
export const AuthContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(AuthReducer, initial_state);

    // Persist the user state in localStorage
    useEffect(() => {
        if (state.user) {
            localStorage.setItem("user", JSON.stringify(state.user));
        } else {
            localStorage.removeItem("user"); // Clear if user is null (on logout)
        }
    }, [state.user]);

    return (
        <AuthContext.Provider
            value={{
                user: state.user,
                loading: state.loading,
                error: state.error,
                dispatch,
            }}
        >
            {children}
        </AuthContext.Provider>
    );
};
